    const [verso, setVerso] = useState(false);
    const [headerLogo, setHeaderLogo] = useState({
        logo: '',
        style: {
            justifyContent: 'flex-start',
            backgroundColor: '#000'
        }
    })
    const [contentTitle, setContentTitle] = useState({
        title: 'Certificado',
        textStyle: {
            fontSize: 40,
            fontWeight: 'bold',
            textAlign: 'center',
            fontFamily: "NunitoRegular",
            color: '#657176'
        }
    })
    const [contentFooter, setContentFooter] = useState({
        text: 'https://4learn.softensistemas.com.br/empresateste/certificado/45645361321321',
        backgroudColor: '#000',
        textStyle: {
            fontSize: 15,
            fontFamily: "NunitoRegular",
            color: "#657176",
            textAlign: 'center',
        },
    })

    const [signatures, setSignatures] = useState([
        {
            img: '',
            label: 'Aluno'
        }
    ])

    const [contentVerso, setContentVerso] = useState({
        title: "Conteúdo programatico",
        details: "Descrição",
        titleStyle: {
            textAlign: 'left',
            fontSize: 25,
            fontFamily: "NunitoRegular",
            fontWeight: 'bold',
            color: "#000"
        },
        detailsStyle: {
            textAlign: 'justify',
            fontSize: 20,
            fontFamily: "NunitoRegular",
            color: "#657176"
        }
    })

    const [contentDescription, setContentDescription] = useState({
        description: textoExemplo,
        address: enderecoExemplo,
        color: '#657176',
        descriptionStyle: {
            fontSize: 17,
            fontFamily: "NunitoRegular",
            textAlign: 'justify'
        },
        addressStyle: {
            fontSize: 17,
            fontFamily: "NunitoRegular",
            textAlign: 'justify'
        }


    })


    return (
        <div className="App">
            <div className='containerCertificado'>


                <PDFViewer className="iframe-pdf">
                    <Document>
                        <Page
                            size="A4"
                            orientation="landscape"
                            style={styles.page}
                            wrap>

                            {/* {!verso ?
                                    <>
                                        <ul>
                                            <li className='certificado-topo'>
                                                {typeHeader === 'Background' ?
                                                    <HeaderBackground2 img={headerBackground} /> :
                                                    <HeaderLogo2 headerLogo={headerLogo} />
                                                }
                                            </li>

                                            <li className='certificado-conteudo'>
                                                <div>
                                                    <CardTitle2 contentTitle={contentTitle} />
                                                    <ContentCard2
                                                        contentDescription={contentDescription}
                                                    />
                                                </div>
                                                <div className="containerAssignatures">
                                                    {signatures.map((index, indice) => (
                                                        <SignatureCard2 signature={index} key={indice} />
                                                    ))}
                                                </div>
                                            </li>

                                            <li className='certificado-footer'>
                                                {typeFooter === 'Background' ?
                                                    <FooterBackground2 img={footerBackground} /> :
                                                    <FooterText2 contentFooter={contentFooter} />
                                                }
                                            </li>
                                        </ul>
                                    </>
