import React, { useState } from 'react';

import NunitoRegular from "./components/fonts/Nunito-Regular.ttf"
import NunitoBold from "./components/fonts/Nunito-Bold.ttf";

import './App.scss';
import { Document, Page, PDFViewer, View, StyleSheet, Font } from '@react-pdf/renderer';

import { SignatureCard, SignatureCard2 } from "./components/SignatureCard";
import { HeaderBackground, HeaderBackground2 } from "./components/HeaderBackground";
import { HeaderLogo, HeaderLogo2 } from "./components/HeaderLogo";
import { CardTitle, CardTitle2 } from "./components/CardTitle";
import { ContentCard, ContentCard2 } from './components/ContentCard';
import { FooterBackground, FooterBackground2 } from "./components/FooterBackground";
import { FooterText, FooterText2 } from './components/FooterText';
import { DetailsContent, DetailsContent2 } from "./components/DetailsContent";
import upload from "./components/img/file.png";
import removeIcon from "./components/img/remover.png";


let textoExemplo = "Certificamos que o Sr. LUCYLENE MOURA DE MENEZES da empresa COMERCIAL BAHIA, concluiu a curso de BRIGADA EMERGENCIAL em conformidades com a NR 23 (Norma Regulamentadora Nº 23 - Proteção contra incêndios regulamentada pela port. do Mb nº 3.214, de 8-6-1978), realizado nos dias 20 e 21/01/2022 com carga horária de 16 horas no total."
let enderecoExemplo = "Rua Antonio Sansone, 335 - José Bonifacio - SP"
function App() {
  const [typeHeader, setTypeHeader] = useState('Logo');
  const [typeFooter, setTypeFooter] = useState('Logo');
  const [headerBackground, setHeaderBackground] = useState('');
