      fontFamily: "NunitoRegular",
      textAlign: 'justify'
    },
    addressStyle: {
      fontSize: 17,
      fontFamily: "NunitoRegular",
      textAlign: 'justify'
    }


  })

  Font.register({
    family: "Nunito", fonts: [
      {
        src: NunitoRegular
      },
      {
        src: NunitoBold,
        fontWeight: 'bold'
      }
    ]
  });

  function selectTypeHeader() {
    const codTypeHeader = document.getElementById("typeHeader").value;
    setTypeHeader(codTypeHeader)
  }

  function selectHeaderJustify() {
    const codHeaderJustify = document.getElementById("headerJustify").value;
    setHeaderLogo((prevState) => ({
      ...prevState,
      style: {
        ...prevState.style,
        justifyContent: codHeaderJustify
