sourcesContent:
import React, { useEffect, useState } from 'react';

import { PDFDownloadLink } from '@react-pdf/renderer';

import { SignatureCard } from "../../components/SignatureCard";
import { HeaderBackground } from "../../components/HeaderBackground";
import { HeaderLogo } from "../../components/HeaderLogo";
import { CardTitle } from "../../components/CardTitle";
import { ContentCard } from '../../components/ContentCard';
import { FooterBackground } from "../../components/FooterBackground";
import { FooterText } from '../../components/FooterText';
import { DetailsContent } from "../../components/DetailsContent";
import upload from "../../components/img/file.png";
import removeIcon from "../../components/img/remover.png";

import { CertificadoPdf } from "../../components/ExportCertificatePdf";

import { certificate } from "../../object";

let textoExemplo = "Certificamos que o Sr. LUCYLENE MOURA DE MENEZES da empresa COMERCIAL BAHIA, concluiu a curso de BRIGADA EMERGENCIAL em conformidades com a NR 23 (Norma Regulamentadora Nº 23 - Proteção contra incêndios regulamentada pela port. do Mb nº 3.214, de 8-6-1978), realizado nos dias 20 e 21/01/2022 com carga horária de 16 horas no total."
let enderecoExemplo = "Rua Antonio Sansone, 335 - José Bonifacio - SP"
export function CertificateEdit() {
    const [header, setHeader] = useState({
        type: "Logo",
        logo: "",
        background: "",
        style: {
            justifyContent: 'flex-start',
            backgroundColor: '#f79'
        }
    });
    const [typeHeader, setTypeHeader] = useState("Logo");
    const [headerLogo, setHeaderLogo] = useState({
        image: '',
        style: {
            justifyContent: 'flex-start',
            backgroundColor: '#fff'
        }
    })
    const [headerBackground, setHeaderBackground] = useState('');

    const [contentTitle, setContentTitle] = useState({
        title: 'Certificado',
        textStyle: {
            fontSize: 40,
            fontWeight: 'bold',
            textAlign: 'center',
            // fontFamily: "NunitoRegular",
            color: '#657176'
        }
    })
    const [contentDescription, setContentDescription] = useState({
        description: textoExemplo,
        address: enderecoExemplo,
        color: '#657176',
        descriptionStyle: {
            fontSize: 17,
            // fontFamily: "NunitoRegular",
            textAlign: 'justify'
        },
        addressStyle: {
            fontSize: 17,
            // fontFamily: "NunitoRegular",
            textAlign: 'justify'
        }
    })
    const [signatures, setSignatures] = useState([
        {
            img: '',
            label: 'Aluno'
        }
    ])

    const [typeFooter, setTypeFooter] = useState("Text");
    const [footerText, setFooterText] = useState({
        text: 'https://4learn.softensistemas.com.br/empresateste/certificado/45645361321321',
        backgroudColor: '#000',
        textStyle: {
            fontSize: 15,
            // fontFamily: "NunitoRegular",
            color: "#657176",
            textAlign: 'center',
        },
    })
    const [footerBackground, setFooterBackground] = useState('');

    const [verso, setVerso] = useState(false);
    const [contentVerso, setContentVerso] = useState({
        title: "Conteúdo programatico",
        details: "Descrição",
        titleStyle: {
            textAlign: 'left',
            fontSize: 25,
            // fontFamily: "NunitoRegular",
            fontWeight: 'bold',
            color: "#000"
        },
        detailsStyle: {
            textAlign: 'justify',
            fontSize: 20,
            // fontFamily: "NunitoRegular",
            color: "#657176"
        }
    })



    function selectTypeHeader() {
        const typeHeader = document.getElementById("typeHeader").value;

        setHeader(prevState => ({
            ...prevState,
            type: typeHeader
        }))
        console.log(header);

    }

    function selectHeaderJustify() {
        const codHeaderJustify = document.getElementById("headerJustify").value;
        setHeaderLogo((prevState) => ({
            ...prevState,
            style: {
                ...prevState.style,
                justifyContent: codHeaderJustify
            }
        }))
    }

    function selectTitleJustify() {
        const codTitleJustify = document.getElementById('contentTitleJustify').value;
        setContentTitle((prevState) => ({
            ...prevState,
            textStyle: {
                ...prevState.textStyle,
                textAlign: codTitleJustify
            }
        }))
    }

    function selectContentFooterJustify() {
        const codContentFooterJustify = document.getElementById('contentFooterJustify').value;
        setFooterText((prevState) => ({
            ...prevState,
            textStyle: {
                ...prevState.textStyle,
                textAlign: codContentFooterJustify
            }
        }))
    }

    function selectTypeFooter() {
        const codTypeFooter = document.getElementById('typeFooter').value;
        setTypeFooter(codTypeFooter);

    }


    function selectContentVersoJustify() {
        const codTitleJustiry = document.getElementById('contentVersoTitleJustify').value;

        setContentVerso((prevState) => ({
            ...prevState,
            titleStyle: {
                ...prevState.titleStyle,
                textAlign: codTitleJustiry
            }
        }))
    }


    function selectContentDescriptionAddressJustify() {
        const contentAddressJustify = document.getElementById('contentDescriptionAddressJustify').value;

        setContentDescription((prevState) => ({
            ...prevState,
            addressStyle: {
                ...prevState.addressStyle,
                textAlign: contentAddressJustify
            }
        }))
    }

    const uploadImage = (e) => {
        if (e.target.files[0].size <= 2457600) {
            return URL.createObjectURL(e.target.files[0])
        } else {
            return ''
        }
    }

    function addSignature() {
        if (signatures.length <= 4) {
            setSignatures((prevState) => ([...prevState, {
                img: '',
                label: 'Teste'
            }]))
        } else {
            alert("Limite de assinaturas atingida")
        }
    }

    function removeSignature(indice) {
        let array = [...signatures];
        array.splice(indice, 1)
        setSignatures(array)
    }

    return (
        <div className="App">
            <div className='containerCertificado'>
                <div className="gerarPdf">

                    <PDFDownloadLink document={<CertificadoPdf certificate={{
                        typeHeader: typeHeader,
                        headerBackground: headerBackground,
                        headerLogo: headerLogo,
                        contentTitle: contentTitle,
                        contentDescription: contentDescription,
                        typeFooter: typeFooter,
                        footerBackground: footerBackground,
                        footerText: footerText,
                        contentVerso: contentVerso,
                        signatures: signatures

                    }} />} fileName="certificado.pdf">
                        {({ blob, url, loading, error }) =>
                            loading ? 'Carregando...' : "Download!"
                        }

                    </PDFDownloadLink>
                </div>

                <div className="certificado">

                    {!verso ?
                        <>
                            <ul>
                                <li className='certificado-topo'>
                                    {typeHeader === 'Background' ?
                                        <HeaderBackground img={headerBackground} /> :
                                        <HeaderLogo headerLogo={headerLogo} />
                                    }
                                </li>

                                <li className='certificado-conteudo'>
                                    <div>
                                        <CardTitle contentTitle={contentTitle} />
                                        <ContentCard
                                            contentDescription={contentDescription}
                                        />
                                    </div>
                                    <div className="containerAssignatures">
                                        {signatures.map((index, indice) => (
                                            <SignatureCard signature={index} key={indice} />
                                        ))}
                                    </div>
                                </li>

                                <li className='certificado-footer'>
                                    {typeFooter === 'Background' ?
                                        <FooterBackground img={footerBackground} /> :
                                        <FooterText footerText={footerText} />
                                    }
                                </li>
                            </ul>
                        </>
                        :
                        <>
                            <ul>
                                <li className='certificado-verso'>
                                    <DetailsContent contentVerso={contentVerso} />
                                </li>
                            </ul>
                        </>
                    }

                </div>

            </div>

            <div className='box-options'>

                <div className='session'>

                    <h4>Edição Certificado</h4>

                    <div className="tab">
                        <button className="tabLinks"
                            style={{ filter: verso ? 'brightness(0.5)' : '' }}
                            onClick={() => setVerso(false)}
                        >Frente</button>

                        <button
                            style={{ filter: !verso ? 'brightness(0.5)' : '' }}
                            className="tabLinks"
                            onClick={() => setVerso(true)}
                        >Verso</button>

                    </div>

                </div>

                {!verso ?
                    <>
                        <div className="session">
                            <h4>Header</h4>

                            <div className="label-input">
                                <label>Tipo </label>
                                <select className='input' id="typeHeader" onChange={() => selectTypeHeader()}>
                                    <option value="Logo">Logo</option>
                                    <option value="Background">Background</option>
                                </select>
                            </div>
                            {typeHeader === 'Background' ?

                                <div className="btn">
                                    {headerBackground &&
                                        <button type="button" className='btn-remove' onClick={() => setHeaderBackground('')}> <img alt='icone' src={removeIcon} /> </button>
                                    }
                                    <div className='box-img'>
                                        <img src={headerBackground || upload} alt="background" />
                                    </div>
                                    <input
                                        id="headerBackground"
                                        className='input-img'
                                        type='file'
                                        name="headerBackground"
                                        accept=".jpeg, .png, .jpg"
                                        onChange={(e) => setHeaderBackground(uploadImage(e))}
                                    />
                                </div>
                                :
                                <>
                                    <div className="btn">
                                        {headerLogo.logo &&
                                            <button type="button" className='btn-remove' onClick={() => setHeaderLogo((prevState) => ({
                                                ...prevState,
                                                'image': ''
                                            }))}> <img src={removeIcon} alt='icone' /> </button>
                                        }
                                        <div className='box-img'>
                                            <img src={headerLogo.image || upload} alt="logo" />
                                        </div>
                                        <input
                                            id="headerLogo"
                                            className='input-img'
                                            type='file'
                                            value={''}
                                            name="headerLogo"
                                            accept=".jpeg, .png, .jpg"
                                            onChange={(e) => setHeaderLogo((prevState) => ({
                                                ...prevState,
                                                'image': uploadImage(e)
                                            }))}
                                        />
                                        <input
                                            id="headerColor"
                                            className='input-color'
                                            type='color'
                                            name="headerColor"
                                            value={headerLogo.style.backgroundColor}
                                            onChange={(e) => setHeaderLogo((prevState) => ({
                                                ...prevState,
                                                style: {
                                                    ...prevState.style,
                                                    backgroundColor: e.target.value
                                                }
                                            }))}
                                        />
                                    </div>

                                    <div className="label-input">
                                        <label>Alinhar </label>
                                        <select className='input' id="headerJustify" onChange={() => selectHeaderJustify()}>
                                            <option value="flex-start">Esquerda</option>
                                            <option value="center">Centro</option>
                                            <option value="flex-end">Direita</option>
                                        </select>
                                    </div>

                                </>

                            }

                        </div>
                        <div className="session">
                            <h4>Título</h4>

                            <div className="label-input">
                                <label>Título</label>
                                <input
                                    id="contentTitle"
                                    className='input'
                                    type="text"
                                    maxLength={40}
                                    name="contentTitle"
                                    value={contentTitle.title}
                                    onChange={(e) => setContentTitle((prevState) => ({
                                        ...prevState,
                                        'title': e.target.value
                                    }))}
                                />
                            </div>

                            <div className="label-input">
                                <label>Cor </label>
                                <input
                                    id="contentTitleCor"
                                    className='input'
                                    type='color'
                                    name="contentTitleCor"
                                    value={contentTitle.textStyle.color}
                                    onChange={(e) => setContentTitle((prevState) => ({
                                        ...prevState,
                                        textStyle: {
                                            ...prevState.textStyle,
                                            color: e.target.value
                                        }
                                    }))}
                                />
                            </div>

                            <div className="label-input">
                                <label>Alinhar </label>
                                <select id="contentTitleJustify" className='input' onChange={() => selectTitleJustify()}>
                                    <option value="center">Centro</option>
                                    <option value="left">Esquerda</option>
                                    <option value="right">Direita</option>
                                </select>
                            </div>

                            <div className="label-input">
                                <label>Fonte .</label>
                                <input
                                    id="contentTitleFonte"
                                    className='input'
                                    type="number"
                                    name="contentTitleFonte"
                                    min={10}
                                    max={42}
                                    value={contentTitle.textStyle.fontSize}
                                    onChange={(e) => setContentTitle((prevState) => ({
                                        ...prevState,
                                        textStyle: {
                                            ...prevState.textStyle,
                                            fontSize: Number(e.target.value)
                                        }
                                    }))}
                                />
                            </div>
                        </div>
                        <div className="session">
                            <h4>Conteúdo</h4>

                            <div className="label-input">
                                <label>Conteúdo </label>
                                <textarea
                                    id="contentDescription"
                                    className='input'
                                    type="textarea"
                                    maxLength={550}
                                    name="contentDescription"
                                    value={contentDescription.description}
                                    onChange={(e) => setContentDescription((prevState) => ({
                                        ...prevState,
                                        description: e.target.value
                                    }))}

                                />
                            </div>

                            <div className="label-input">
                                <label>Fonte Conteúdo </label>
                                <input
                                    id="contentDescriptionDescriptionStyleFont"
                                    className='input'
                                    type="number"
                                    min={10}
                                    max={20}
                                    name="contentDescriptionDescriptionStyleFont"
                                    value={contentDescription.descriptionStyle.fontSize}
                                    onChange={(e) => setContentDescription((prevState) => ({
                                        ...prevState,
                                        descriptionStyle: {
                                            ...prevState.descriptionStyle,
                                            fontSize: Number(e.target.value)
                                        }
                                    }))}

                                />
                            </div>

                            <div className="label-input">
                                <label>Endereço </label>
                                <textarea
                                    id="contentDescriptionAddress"
                                    className='input'
                                    type="textarea"
                                    maxLength={80}
                                    name="contentDescriptionAddress"
                                    value={contentDescription.address}
                                    onChange={(e) => setContentDescription((prevState) => ({
                                        ...prevState,
                                        address: e.target.value
                                    }))}
                                />
                            </div>

                            <div className="label-input">
                                <label>Fonte Endereço </label>
                                <input
                                    id="contentDescriptionAddressStyleFont"
                                    className='input'
                                    type="number"
                                    min={10}
                                    max={20}
                                    name="contentDescriptionAddressStyleFont"
                                    value={contentDescription.addressStyle.fontSize}
                                    onChange={(e) => setContentDescription((prevState) => ({
                                        ...prevState,
                                        addressStyle: {
                                            ...prevState.addressStyle,
                                            fontSize: Number(e.target.value)
                                        }
                                    }))}

                                />
                            </div>

                            <div className="label-input">
                                <label>Alinhar Endereço </label>
                                <select className='input' id="contentDescriptionAddressJustify" onChange={() => selectContentDescriptionAddressJustify()}>
                                    <option value="left">Esquerda</option>
                                    <option value="center">Centro</option>
                                    <option value="right">Direita</option>
                                </select>
                            </div>

                            <div className="label-input">
                                <label>Cor </label>
                                <input
                                    id="contentDescriptionColor"
                                    className='input'
                                    type='color'
                                    name="contentDescriptionColor"
                                    value={contentDescription.color}
                                    onChange={(e) => setContentDescription((prevState) => ({
                                        ...prevState,
                                        color: e.target.value
                                    }))}
                                />
                            </div>
                        </div>

                        <div className="session">
                            <h4>Assinatura <button className='btn-assinatura' type="button" onClick={addSignature}> Adicionar </button></h4>

                            {signatures.map((index, indice) => (
                                <>
                                    <div className="label-input" key={indice}>
                                        <label>Label </label>
                                        <input
                                            id={`signatureLabel${indice}`}
                                            className='input'
                                            type="text"
                                            maxLength={30}
                                            name={`signatureLabel${indice}`}
                                            value={index.label}
                                            onChange={(e) => {
                                                const newState = [...signatures];
                                                newState[indice].label = e.target.value;
                                                setSignatures(newState)
                                            }}

                                        />
                                        <button type="button" className='btn-remove' onClick={() => removeSignature(indice)}> <img src={removeIcon} alt='icon' /> </button>
                                    </div>

                                    <div className="btn">

                                        {signatures[indice].img &&
                                            <button type="button" className='btn-remove' onClick={() => {
                                                const newState = [...signatures];
                                                newState[indice].img = ''
                                                setSignatures(newState)
                                            }}> <img src={removeIcon} alt='icon' /> </button>
                                        }
                                        <div className='box-img'>
                                            <img src={signatures[indice].img || upload} alt='img' />
                                        </div>
                                        <input
                                            id="signatureImg"
                                            className='input-img'
                                            type='file'
                                            value={''}
                                            name="signatureImg"
                                            accept=".jpeg, .png, .jpg"
                                            onChange={(e) => {
                                                const newState = [...signatures];
                                                newState[indice].img = uploadImage(e);
                                                setSignatures(newState)
                                            }}
                                        />
                                    </div>
                                </>
                            ))}

                        </div>
                        <div className="session">
                            <h4>Footer</h4>
                            <div className="label-input">
                                <label>Tipo </label>
                                <select className='input' id="typeFooter" onChange={() => selectTypeFooter()}>
                                    <option value="Texto">Texto</option>
                                    <option value="Background">Background</option>
                                </select>
                            </div>

                            {typeFooter === 'Background' ?

                                <div className="btn" style={{ marginBottom: 50 }}>
                                    {footerBackground &&
                                        <button type="button" className='btn-remove' onClick={() => setFooterBackground('')}> <img src={removeIcon} alt='icon' /> </button>
                                    }
                                    <div className='box-img'>
                                        <img src={footerBackground || upload} alt='background' />
                                    </div>
                                    <input
                                        id="footerBackground"
                                        className='input-img'
                                        type='file'
                                        name="footerBackground"
                                        accept=".jpeg, .png, .jpg"
                                        onChange={(e) => setFooterBackground(uploadImage(e))}
                                    />
                                </div>
                                :
                                <>
                                    <div className="label-input">
                                        <label>Texto</label>
                                        <textarea
                                            id="footerText"
                                            className='input'
                                            type="text"
                                            name="footerText"
                                            maxLength={300}
                                            value={footerText.text}
                                            onChange={(e) => setFooterText((prevState) => ({
                                                ...prevState,
                                                'text': e.target.value
                                            }))}
                                        />
                                    </div>

                                    <div className="label-input">
                                        <label>Alinhar </label>
                                        <select className='input' id="contentFooterJustify" onChange={() => selectContentFooterJustify()}>
                                            <option value="center">Centro</option>
                                            <option value="left">Esquerda</option>
                                            <option value="right">Direita</option>
                                        </select>
                                    </div>

                                    <div className="label-input">
                                        <label>Fonte </label>
                                        <input
                                            id="footerTextFonte"
                                            className='input'
                                            type="number"
                                            min={10}
                                            max={20}
                                            name="footerTextFonte"
                                            value={footerText.textStyle.fontSize}
                                            onChange={(e) => setFooterText((prevState) => ({
                                                ...prevState,
                                                textStyle: {
                                                    ...prevState.textStyle,
                                                    fontSize: Number(e.target.value)
                                                }
                                            }))}
                                        />
                                    </div>

                                    <div className="label-input">
                                        <label>Cor Texto </label>
                                        <input
                                            id="footerTextCorTexto"
                                            className='input'
                                            type='color'
                                            name="footerTextCorTexto"
                                            value={footerText.textStyle.color}
                                            onChange={(e) => setFooterText((prevState) => ({
                                                ...prevState,
                                                textStyle: {
                                                    ...prevState.textStyle,
                                                    color: e.target.value
                                                }
                                            }))}
                                        />
                                    </div>

                                    <div className="label-input" style={{ marginBottom: 50 }}>
                                        <label>Background Color </label>
                                        <input
                                            id="contentFooterBackgroundCor"
                                            className='input'
                                            type='color'
                                            name="contentFooterBackgroundCor"
                                            value={footerText.backgroudColor}
                                            onChange={(e) => setFooterText((prevState) => ({
                                                ...prevState,
                                                'backgroudColor': e.target.value

                                            }))}
                                        />
                                    </div>

                                </>
                            }
                        </div>

                    </>
                    :
                    <>
                        <div className="session">
                            <h4>Título</h4>
                            <div className="label-input">
                                <label>Título </label>
                                <input
                                    id="contentVersoTitle"
                                    className='input'
                                    type="text"
                                    maxLength={50}
                                    name="contentVersoTitle"
                                    value={contentVerso.title}
                                    onChange={(e) => setContentVerso((prevState) => ({
                                        ...prevState,
                                        title: e.target.value
                                    }))}
                                />
                            </div>

                            <div className="label-input">
                                <label>Cor </label>
                                <input
                                    id="contentVersoTitleCor"
                                    className='input'
                                    type='color'
                                    name="contentVersoTitleCor"
                                    value={contentVerso.titleStyle.color}
                                    onChange={(e) => setContentVerso((prevState) => ({
                                        ...prevState,
                                        titleStyle: {
                                            ...prevState.titleStyle,
                                            color: e.target.value
                                        }
                                    }))}
                                />
                            </div>

                            <div className="label-input">
                                <label>Alinhar </label>
                                <select className='input' id="contentVersoTitleJustify" onChange={() => selectContentVersoJustify()}>
                                    <option value="left">Esquerda</option>
                                    <option value="center">Centro</option>
                                    <option value="right">Direita</option>
                                </select>
                            </div>

                            <div className="label-input">
                                <label>Fonte </label>
                                <input
                                    id="contentVersoTitleSize"
                                    className='input'
                                    type="number"
                                    min={10}
                                    max={45}
                                    name="contentVersoTitleSize"
                                    value={contentVerso.titleStyle.fontSize}
                                    onChange={(e) => setContentVerso((prevState) => ({
                                        ...prevState,
                                        titleStyle: {
                                            ...prevState.titleStyle,
                                            fontSize: Number(e.target.value)
                                        }
                                    }))}
                                />
                            </div>

                        </div>

                        <div className='session'>
                            <h4>Conteúdo</h4>

                            <div className="label-input">
                                <label>Conteúdo</label>
                                <textarea
                                    id="contentVersoDetails"
                                    className='input'
                                    type="textarea"
                                    name="contentVersoDetails"
                                    maxLength={1000}
                                    value={contentVerso.details}
                                    onChange={(e) => setContentVerso((prevState) => ({
                                        ...prevState,
                                        details: e.target.value
                                    }))}
                                />
                            </div>

                            <div className="label-input">
                                <label>Cor </label>
                                <input
                                    id="contentVersoDetailsCor"
                                    className='input'
                                    type="color"
                                    name="contentVersoDetailsCor"
                                    value={contentVerso.detailsStyle.color}
                                    onChange={(e) => setContentVerso((prevState) => ({
                                        ...prevState,
                                        detailsStyle: {
                                            ...prevState.detailsStyle,
                                            color: e.target.value
                                        }
                                    }))}
                                />
                            </div>

                            <div className="label-input">
                                <label>Fonte </label>
                                <input
                                    id="contentVersoDetailsSize"
                                    className='input'
                                    type="number"
                                    min={10}
                                    max={20}
                                    name="contentVersoDetailsSize"
                                    value={contentVerso.detailsStyle.fontSize}
                                    onChange={(e) => setContentVerso((prevState) => ({
                                        ...prevState,
                                        detailsStyle: {
                                            ...prevState.detailsStyle,
                                            fontSize: Number(e.target.value)
                                        }
                                    }))}
                                />
                            </div>
                        </div>
                    </>
                }

            </div>
        </div>
    );
}
