import React from "react";

import { Document, Page, View, StyleSheet } from '@react-pdf/renderer';

import { SignatureCardPdf } from "../SignatureCard";
import { HeaderBackgroundPdf } from "../HeaderBackground";
import { HeaderLogoPdf } from "../HeaderLogo";
import { CardTitlePdf } from "../CardTitle";
import { ContentCardPdf } from '../ContentCard';
import { FooterBackgroundPdf } from "../FooterBackground";
import { FooterTextPdf } from '../FooterText';
import { DetailsContentPdf } from "../DetailsContent";

export function CertificadoPdf({ certificate }) {
    const {
        typeHeader,
        headerBackground,
        headerLogo,
        contentTitle,
        contentDescription,
        signatures,
        typeFooter,
        footerBackground,
        contentFooter,
        contentVerso
    } = certificate;

    return (
        <Document>
            <Page
                size="A4"
                orientation="landscape"
                style={styles.page}
                wrap>
                {/* header */}
                <View style={styles.certificateTop}>
                    {typeHeader === 'Background' ?
                        <HeaderBackgroundPdf img={headerBackground} /> :
                        <HeaderLogoPdf headerLogo={headerLogo} />
                    }
                </View>
                {/* content */}
                <View style={styles.certificateContent}>
                    <View>
                        <CardTitlePdf contentTitle={contentTitle} />
                        <ContentCardPdf
                            contentDescription={contentDescription}
                        />
                    </View>

                    <View style={styles.containerSignature}>
                        {signatures.map((index, indice) => (
                            <SignatureCardPdf signature={index} key={indice} />
                        ))}
                    </View>

                </View>
                {/* footer */}
                <View style={styles.certificadoFooter}>
                    {typeFooter === 'Background' ?
                        <FooterBackgroundPdf img={footerBackground} /> :
                        <FooterTextPdf contentFooter={contentFooter} />
                    }
                </View>
                {/* verso */}
                <DetailsContentPdf contentVerso={contentVerso} />

            </Page>
        </Document>
    )
}
const styles = StyleSheet.create({
    page: {
        flex: 1,
    },
    containerSignature: {
        width: '100%',
        flexDirection: 'row',
        alignContent: 'center',
        justifyContent: 'space-around'
    },
    certificateTop: {
        height: '15%'

    },
    certificateContent: {
        height: '70%',
        paddingVertical: 25,
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'space-between'

    },
    certificadoFooter: {
        height: '15%'

    }
})
