export const certificate = {
    header: {
        type: "Logo",
        logo: "",
        background: "",
        style: {
            justifyContent: 'flex-start',
            backgroundColor: '#88aa'
        }
    },
    body: {
        title: "Certificado de conclusão",
        titleStyle: {
            fontSize: 40,
            fontWeight: 'bold',
            textAlign: 'center',
            // fontFamily: "NunitoRegular",
            color: '#657176'
        },
        descrition: "Certificamos que...",
        address: "José Boni.....",
        color: "#fff",
        descritionStyle: {
            fontSize: 17,
            // fontFamily: "NunitoRegular",
            textAlign: 'justify'
        },
        addressStyle: {
            fontSize: 17,
            // fontFamily: "NunitoRegular",
            textAlign: 'justify'
        },
        signatures: [
            {
                image: "",
                label: ""
            },
            {
                image: "",
                label: ""
            }

        ]
    },
    footer: {
        type: "Texto",
        text: "Teste footer",
        background: "",
        backgroudColor: "#88aa",
        textStyle: {
            fontSize: 15,
            // fontFamily: "NunitoRegular",
            color: "#fff",
            textAlign: 'center',
        }
    },
    verse: {
        title: "Conteúdo",
        details: "Descrição...",
        titleStyle: {
            textAlign: 'left',
            fontSize: 25,
            // fontFamily: "NunitoRegular",
            fontWeight: 'bold',
            color: "#000"
        },
        detailsStyle: {
            textAlign: 'justify',
            fontSize: 20,
            // fontFamily: "NunitoRegular",
            color: "#657176"
        }

    },
}
