import React from "react";
import { View, StyleSheet, Text } from "@react-pdf/renderer";

import "./index.scss"

export function FooterTextPdf({ contentFooter }) {

    return (
        <View style={[styles.footerTextContainer, { backgroundColor: contentFooter.backgroudColor }]}>
            <Text style={contentFooter.textStyle}>{contentFooter.text}</Text>
        </View>

    )
}

export function FooterText({ contentFooter }) {

    return (
        <div className="footerTextContainer" style={contentFooter.textStyle}>
            <p style={contentFooter.textStyle}>
                {contentFooter.text}
            </p>
        </div>

    )
}

const styles = StyleSheet.create({
    footerTextContainer: {
        display: 'flex',
        flexDirection: 'column',
        width: '100%',
        height: '100%',
        justifyContent: 'center',
        paddingHorizontal: 40,
        overflow: 'hidden'
    },

})
