import React from "react";
import { Image, Text, View, StyleSheet } from '@react-pdf/renderer';

import "./index.scss";

export function SignatureCard({ signature }) {


    return (
        <View style={styles.signatureCardContainer}>

            <View style={styles.containerImg}>
                {signature.img === '' ?
                    <View style={styles.containerImg} />
                    :
                    <Image src={signature.img} />
                }
            </View>

            <View style={styles.containerLabel}>
                <Text style={styles.labelText}>{signature.label}</Text>
            </View>

        </View>
    )

}

export function SignatureCard2({ signature }) {

    return (
        <div className="signatureCardContainer">
            <div className='signatureCardContainerImg'>
                {signature.img === '' ?
                    <div className='signatureCardContainerImg'>
                    </div>
                    :
                    <img src={signature.img} />
                }
            </div>

            <div className="signatureCardContainerLabel">
                <p >{signature.label}</p>
            </div>


        </div>
    )
}

const styles = StyleSheet.create({
    signatureCardContainer: {
        display: 'flex',
        flexDirection: 'column',
        width: 120,
        height: 50,
        alignItems: 'center',
        justifyContent: 'flex-end'

    },
    containerImg: {
        width: 'auto',
        height: 'auto',
    },
    containerLabel: {
        width: '100%',
        justifyContent: 'flex-end',
        alignItems: 'center',
        borderTopWidth: 0.5,

    },
    labelText: {
        fontSize: 10,
    }

})
