import React from "react";
import { View, Text, StyleSheet } from "@react-pdf/renderer";

import "./index.scss";

export function ContentCard({ content, address, justify, contentTextStyle, addressTextStyle }) {
    return (
        <View style={styles.container}>
            <Text style={contentTextStyle}>{content}</Text>
            <View style={[styles.address, { alignItems: justify }]}>
                <Text style={addressTextStyle}>{address}</Text>
            </View>

        </View>
    )
}

export function ContentCard2({ contentDescription }) {

    return (
        <div className="contentCardContainer" style={{color: contentDescription.color}}>
            <p style={contentDescription.descriptionStyle}>{contentDescription.description}</p>

            <p style={contentDescription.addressStyle}>{contentDescription.address}</p>
        </div>
    )

}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        width: '100%',
        alignItems: 'center',
        justifyContent: 'space-between',
        paddingHorizontal: 50,
    },

    address: {
        width: '100%',
    }
})
